html:
{% if letter_head and not no_letterhead -%}
    <div class="letter-head">{{ letter_head }}</div>
<hr>    
{%- endif %}
<h2><div class="text-center"> {{ "FACTURA" }} </div></h2>
<h4><div class="text-left">N.: {{doc.name }} </div></h4>

<div class="row">
  <div class="col-xs-8" ><h4><b>Cliente: {{ doc.customer_name}}</b></h4>  
	<div class="row">
        <div class="col-xs-6" ><b>Folha de Obra: </b>{{ doc.project}}</div>
        {% if doc.status != 'Unpaid' and doc.status != 'Paid' -%}
		<div class="col-xs-6" ><b>Estado: </b>{{ _(doc.status) }}</div>
	{% endif -%}
    </div>

  </div>
  <div class="col-xs-4" ><b>Data:</b> {{ doc.get_formatted("posting_date") }} </div>

</div>


<p><br></p>
{% if doc.project -%}
{% set carro = frappe.get_doc("Folha de Obras", doc.project) %}




<div class="row">
  <div class="col-xs-8" >
		<style>
		table#t01 {
			font-family: arial, sans-serif;
			font-size:10px;
			border-collapse: collapse;
			width: 70%;
		}


		td, th {
			border: 1px solid #dddddd;
			text-align: center;
			padding: 8px;
		}
		td#td01 {
			border: 1px solid #dddddd;
			text-align: right;
			padding: 8px;
		}


		tr:nth-child(even) {
			background-color: #dddddd;
		}

		</style>

		<table id="t01">
		  <tr>
			<td ><b>Marca/Modelo</b></td>
			<td> {{ carro.marca_veiculo}} </td>
		  </tr>
		  <tr>
			<td><b>Chassi</b></td>
			<td>{{ carro.numero_chassi}} </td>
		  </tr>
		  <tr>
			<td><b>Matricula</b></td>
			<td>{{ carro.matricula_veiculo}}</td>
		  </tr>
		</table>
	</div>
</div>
{% endif -%}

<p><br></p>

{%- set n = 10 -%} <!-- number of items in one page -->
{%- set page = 0 -%} <!-- this is page counter -->

{% if doc.items%}

<table>
            <tr>
                <th>Nr</th>
				<th style="text-center">Material Aplicado</th>
				<th style="text-center">Qtd</th>
				<th style="text-center">Preço</th>
				<th style="text-center">Total</th>				
			</tr>


            {%- if (doc.items|length <= n) -%} 
				{%- set page_size = 1 -%}

            {%- elif ((doc.items|length > n) and (doc.items|length <= (n*2))) -%} 
				{%- set page_size = 2 -%}
            {%- elif ((doc.items|length % n == 0)) -%} 
				{%- set page_size = doc.items|length // n -%}
            {% else %}
				{%- set page_size = (doc.items|length // n)+1 -%}
            {%- endif -%} 

            {%- for i in range(0,page_size * n) -%}

				{%- set row = doc.items[i] -%}

				{% if row != null -%}

				<!-- item row -->
					<tr>              
						<td style="width: 2%;">{{ row.idx }}</td>
						<td style="text-center; width: 20%;">{{ row.item_code }} </td>
						<td style="text-center; width: 2%;">{{ row.qty }}</td>				
						<td id="td01" style="text-right; width: 8%;">{{ row.get_formatted("rate", doc)}}</td>
						<td id="td01" style="text-right; width: 8%;">{{ row.get_formatted("amount", doc)}}</td>               
					</tr>
				{% else %}
					<tr>
                            <td> &nbsp; </td>
							<td> &nbsp; </td>
							<td> &nbsp; </td>
							<td> &nbsp; </td>
							<td> &nbsp; </td>
                            ...
                    </tr>
				{%- endif %}

				<!-- page-break here -->
				{% if ((i+1) >= n) and ((i+1) % n == 0) and ((i+1) < doc.items|length) -%}
					 {%- set page = page + 1 -%}    
</table>

<div id="footer-html" class="text-center small visible-pdf">
        {% if letter_head and not no_letterhead -%}
	{% set ff = frappe.get_doc("Letter Head", doc.letter_head) %}
        <div class="text-center small letter-head-footer">
            {{ ff.footer }}
        </div>
        {% endif %}

        <p class="text-left small visible-pdf"> Processado por Computador </p>
        <p class="text-center small page-number visible-pdf">
            {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
        </p>
    </div>


<div class="page-break"></div>     

<!-- Items table header -->
<p><br></p>
<table>
           <tr>
                <th>Nr</th>
				<th style="text-center">Material Aplicado</th>
				<th style="text-center">Qtd</th>
				<th style="text-center">Preço</th>
				<th style="text-center">Total</th>				
			</tr>

{%- endif %}

{%- endfor -%}
</table>


{% endif %}



<div class="row">
  <div class="col-xs-6 text-right" ><h4><b>TOTAL GERAL KZ:</b></h4>    </div>
  <div class="col-xs-6 text-right" ><h5><b> </b> {{ doc.get_formatted("total") }}</h5>  </div>
 </div>
<div class="row">
	{% if doc.contra_valor >= 1 -%}
  		<div class="col-xs-6 text-right" ><h6><b>Contra Valor Usd: </b></h6>  </div>
		<div class="col-xs-6 text-right" ><h6><b> </b> {{ doc.get_formatted("contra_valor") }}</h6> </div>  
  {%- endif %}
 </div>

{% if doc.terms != None -%} 
	<h5><div class="text-left small">OBS: {{doc.terms}} </div></h5>
{% endif -%}
